mapper.update:
update talent_b
	    set
	        talb_Title = #{talb_Title},
	        talb_Kinds = #{talb_Kinds},
	        talb_Content = #{talb_Content}

	    where talb_Code = ${talb_Code}

mapper.select:
	select talb_Code, talb_Id, talb_Title, talb_Kinds, talb_Content, phone_Num, talb_Date
    		from talent_b
        		where talb_Code = #{talb_Code}
